mapper.update:
update annual
set an_acknowledge=#{an_acknowledge}
where an_no=#{an_no}

mapper.select:
select p_no, e_no, to_char(p_date, 'yyyy/MM/dd') as p_date, p_basepay, p_netpay
from PayStub
where p_no = #{p_no}

select pb_no, e_no, pb_cpn, pb_Name, pb_Phone_Number as pb_phoneNumber, pb_Rank, pb_Email
from PhoneBook
where pb_no = #{pb_no}
