mapper.insert:
insert into reserve(
	 r_no, mr_no, r_date, r_starttime, r_endtime, meetingtitle 
)values(
r_no_seq.nextval, #{mr_no}, sysdate, #{r_starttime}, #{r_endtime}, #{meetingtitle}
)

mapper.update:
update reserve set
meetingtitle = #{meetingtitle}, r_starttime = #{r_starttime}, r_endtime = #{r_endtime}  
where r_no = #{r_no}
